var BasicIdToken = require('../src/oicMsg/tokenProfiles/basicIdToken');
var assert = require('chai').assert;
var jwtDecoder = require('../src/oicMsg/jose/jwt/decode');
var readSync = require('read-file-relative').readSync;

describe('When signing an x509 certificate', function() {
  var priv = readSync('./keys/rolandPriv.pem');
  var pub = readSync('./keys/rolandPub.pem');

  var clockTimestamp = 1511783267;
  var basicIdToken = new BasicIdToken(
      {iss: 'issuer', sub: 'subject', iat: clockTimestamp, jti: 'jwtid'});
  basicIdToken.addOptionalClaims({'foo': 'bar', 'aud': 'audience'});
  basicIdToken.setNoneAlgorithm(true);

  it('should work with the corresponding pub key 1', function() {
    basicIdToken.toJWT(priv, {algorithm: 'RS256'}).then(function(token) {
      jwtDecoder.prototype.verifyJwtSign(
          token, pub, basicIdToken, {
            'clockTimestamp': clockTimestamp,
            jwtid: 'jwtid',
            algorithm: 'HS256'
          },
          'base64');
    });
  });
});

describe('When signing an x509 certificate', function() {
  var priv = readSync('./keys/example_key.pem');
  var pub = readSync('./keys/example_pub_key.pem');

  var clockTimestamp = 1511783267;
  var basicIdToken = new BasicIdToken(
      {iss: 'issuer', sub: 'subject', iat: clockTimestamp, jti: 'jwtid'});
  basicIdToken.addOptionalClaims({'foo': 'bar', 'aud': 'audience'});
  basicIdToken.setNoneAlgorithm(true);

  it('should work with the corresponding pub key 2', function() {
    basicIdToken.toJWT(priv, {algorithm: 'RS256'}).then(function(token) {
      jwtDecoder.prototype
          .verifyJwtSign(
              token, pub, basicIdToken, {
                'clockTimestamp': clockTimestamp,
                jwtid: 'jwtid',
                algorithm: 'HS256'
              },
              'base64')
          .then(function(verified) {
            assert.equal(verified, true);
          });
    });
  });

  it('should not work with wrong pub key 1', function() {
    basicIdToken.toJWT(priv, {algorithm: 'RS256'}).then(function(token) {
      var verified = jwtDecoder.prototype.verifyJwtSign(
          token, pub, basicIdToken, {
            'clockTimestamp': clockTimestamp,
            jwtid: 'jwtid',
            algorithm: 'HS256'
          },
          'base64');
      assert.equal(verified, false);
    });
  });
});

